/*
    The inputPropertyValues file should be in the format <propertyName>,<oldValue>,<newValue> ex.:
        propertyName,oldVal,newVal
        propertyName1,oldVal1,newVal1
*/

splitted = str:split(vals, ',');

if(arr:getLength(splitted) == 3) {
    propertyName = splitted[0];
    oldValue = splitted[1];
    newValue = splitted[2];
} else {
    propertyName = "";
    oldValue = "";
    newValue = "";
}

/*
    The inputXPath file should be in the form <propertyName>;<xPathQuery>;<propertyValue> ex.:
        propertyName;/jcr:root/content/swisscom/de//*[@sling:resourceType='foundation/components/parsys']/*[not(@sling:resourceType)];newVal
        propertyName1;/jcr:root/content/swisscom/de//*[(jcr:like(@sling:resourceType, 'swisscom/components/content/text'))];newVal1
*/

splitted = str:split(vals, ';');

if(arr:getLength(splitted) == 3) {
    propertyName = splitted[0];
    xPathQuery = splitted[1];
    newValue = splitted[2];
} else {
    propertyName = "";
    xPathQuery = "";
    newValue = "";
}
